import { Structure, WriterFunction } from 'ts-morph';
declare type FlatType<T> = T extends Array<infer R> ? R : T;
declare type NeverIfIsNeverArray<T> = T extends Array<never> ? never : T;
declare type ExtractPrimitive<T> = Extract<FlatType<Exclude<T, WriterFunction>>, string | number | boolean>;
/**
 * Делаем из объекта пригодный объект для поиска
 *
 * @example
 * // Тип
 * {
 *   named: string;
 *   flag: boolean;
 *   keys: string[]
 *   objects: {}[]
 * }
 *
 * // Query
 *
 * {
 *   named: string | string[];
 *   flag: boolean | boolean[];
 *   keys: string | string[];
 *   objects: never;
 * }
 *
 */
export declare type Query<T extends Structure> = Partial<{
    [key in keyof T]: ExtractPrimitive<T[key]> | NeverIfIsNeverArray<ExtractPrimitive<T[key]>[]>;
}>;
export declare function matchQuery<T extends Structure>(value: T, query?: Query<T>): boolean;
export {};
